const GuessIntroduction = (props) => {
    const style = {
        width: "50%",
        marginRight: "0 auto",
        textAlign: "left",
        display:"flex"
    }
    return (
        <div style = {style}>
            <p>
            <h2>{props.name}</h2>
            
            <body>{props.text}</body>
            </p>
        </div>
    );
};

export default GuessIntroduction;
