import React from 'react'
import { Player, ControlBar, PlaybackRateMenuButton } from 'video-react'

const VideoPlayer = (props) => {
  return (
    <Player
      ref={c=>{
        this.player = c;
      }}
      playsInline>
      <ControlBar>
        <PlaybackRateMenuButton order={7} rates={[2, 1.5, 1.0, 0.5]} />
      </ControlBar>
      <source
        src={props.VideoURL}
        type="video/mp4"
      />
    </Player>
  );
}

export default VideoPlayer;
